import React, { Component } from 'react'

class Todos extends Component {
  state = {
    cross: false
  }
  handleClick = (e, id) => {
    this.setState({
      cross: !this.state.cross
    })
    console.log(this.props.todos)
    console.log(id)
  }
  render() {
    const todoList = this.props.todos.length ? (
      this.props.todos.map(todo => {
        let crossout = this.state.cross ? "crossed" : "not-crossed";
        return (
          <div className="collection-item" key={todo.id}>
            <span className={crossout} onClick={() => {this.handleClick(todo.id)}}>{todo.content}</span>
            <button onClick={() => {this.props.deleteTodo(todo.id)}}>Delete</button>
            
          </div>
        )
      })
    ) : (
      <p className="center">You have no Todo's left!</p>
    )
    return (
      <div className="todos collection">
        {todoList}
      </div>
    )
  }
}

export default Todos
