import React, { Component } from 'react';
import Todos from './Todos'
import AddTodo from './AddTodo'
class App extends Component {
  constructor(){
    super();

    this.state = {
         black: true
    }
}
  state = {
    todos: [
      {id: 1, content: 'Buy Chocolate'},
      {id: 2, content: 'Play Games'}
    ]
  }
  deleteTodo = (id) => {
    const todos = this.state.todos.filter(todo => {
      return todo.id !== id
    });
    this.setState({
      todos
    })
  }
  addTodo = (todo) => {
    todo.id = Math.random();
    let todos = [...this.state.todos, todo];
    this.setState({
      todos
    })
  }
  changeColor(){
    this.setState({black: !this.state.black})
  }

  render() {
    return (
      <div className="todo-app container">
        <h1 className="center">Todo's</h1>
        <Todos todos={this.state.todos} deleteTodo={this.deleteTodo} changeColor={this.changeColor}/>
        <AddTodo addTodo={this.addTodo}/>
      </div>
    );
  }
}

export default App;
