import React, { Component } from 'react';
import Todos from './Todos'
import AddTodo from './AddTodo'
class App extends Component {

  state = {
    black: false,
    todos: [
      {id: 1, content: 'Buy Chocolate'},
      {id: 2, content: 'Play Games'},
      {id: 3, content: 'Drink Water'}
    ]
  }
  deleteTodo = (id) => {
    const todos = this.state.todos.filter(todo => {
      return todo.id !== id
    });
    this.setState({
      todos
    })
  }
  addTodo = (todo) => {
    todo.id = Math.random();
    let todos = [...this.state.todos, todo];
    this.setState({
      todos
    })
  }
  changeCross = () =>{
    const todos = this.state.todos;
    for (let x in todos) {
      let todo = todos[x];
      let ids = todo.id;
      console.log(ids);
    }
    // this.setState({black: !this.state.black})
  }

  render() {
    return (
      <div className="todo-app container">
        <h1 className="center">Todo's</h1>
        <Todos todos={this.state.todos} black={this.state.black} deleteTodo={this.deleteTodo} changeCross={this.changeCross}/>
        <AddTodo addTodo={this.addTodo}/>
      </div>
    );
  }
}

export default App;
